import React from "react";
import { Link, } from "react-router-dom";
import { Button, Card, Segment,} from "semantic-ui-react";
import { getNotes, } from "../reducers/notes";

import Note from "./Note";
import { connect, } from 'react-redux';

class Home extends React.Component {

componentDidMount() {
  this.props.dispatch(getNotes())
}


render () {
  return (
  <div style={{marginTop: "15px"}}>
    <Segment>
      <h1>Home</h1>
      <Button as={Link} to="/newnote" style={{marginTop: "20px", position: "absolute", bottom: "5px",}}>New Note</Button>

    </Segment>
    <Card.Group>

    { this.props.notes.map( (t) => {
      return (
       <Note key={t.id} {...t} />
      )
    })
  }
  </Card.Group>
    <Button as={Link} to="/newnote" style={{marginTop: "20px"}}>New Note</Button>
  </div>
)}
}


const mapStateToProps = (state) => {
  return { notes: state.notes, };
}

export default connect(mapStateToProps)(Home);
