import axios from 'axios';

export const getNotes = () => {
  return(dispatch) => {
    axios.get('/api/notes')
      .then( res => dispatch())
  }
}



export default ( state = [], action ) => {
  switch(action.type) {
    case 'NOTES':
      return action.note
    case 'ADD_NOTE':
      return [action.note, ...state];
    case 'DELETE_NOTE':
      return state.filter( note => {
        if (note.id !== action.id )
          return note
      })
    case 'EDIT_NOTE':
      return state.map( note => {
        if (note.id === action.note.id ) {
          return action.note
        }
        else {return note}
      })
    default:
      return state
  }
}
